import TodoForm from './TodoForm'
import Todo from './Todo';

function Todolist() {
    const [todos, setTodos] = useState([]);

    const addTodo = todo => {
        if(!todo.text || /^\s*$/.test(todo.text)) {
            return
        }

        const newTodos = [todo, ...todos]

        setTodos(newTodos)
    };

  return (
    <div>
      <h1> What's the plan for today? </h1>
